mapper.select:
SELECT FACT_CD ,EQU_CD ,PROC_CD ,(SELECT PROC_NM FROM MES_PROCESS_INFO WHERE MEI.PROC_CD = PROC_CD) PROC_NM ,EQU_NM ,EQU_NO ,EQU_TYPE ,EQU_MODEL ,EQU_NATION ,EQU_COMPANY ,EQU_YN ,EQU_NOTICE ,EQU_INDATE ,EQU_INUSER ,EQU_UPDATE ,EQU_UPUSER FROM MES_EQUIPMENT_INFO MEI WHERE EQU_YN = 'Y'
AND PROC_CD = #{proc_cd}
AND ( EQU_CD LIKE CONCAT('%', #{search_text}, '%') OR EQU_NM LIKE CONCAT('%', #{search_text}, '%') OR EQU_NO LIKE CONCAT('%', #{search_text}, '%') OR EQU_TYPE LIKE CONCAT('%', #{search_text}, '%') OR EQU_MODEL LIKE CONCAT('%', #{search_text}, '%') OR EQU_NATION LIKE CONCAT('%', #{search_text}, '%') OR EQU_COMPANY LIKE CONCAT('%', #{search_text}, '%') )

SELECT FACT_CD ,EQU_CD ,PROC_CD ,EQU_NM ,EQU_NO ,EQU_TYPE ,EQU_MODEL ,EQU_NATION ,EQU_COMPANY ,EQU_YN ,EQU_NOTICE ,EQU_INDATE ,EQU_INUSER ,EQU_UPDATE ,EQU_UPUSER FROM MES_EQUIPMENT_INFO WHERE EQU_YN = 'Y'
AND EQU_CD = #{equ_cd}
AND PROC_CD = #{proc_cd}

mapper.insert:
INSERT INTO MES_EQUIPMENT_INFO
(
    FACT_CD
    ,EQU_CD
    ,PROC_CD
    ,EQU_NM
    ,EQU_NO
    ,EQU_TYPE
    ,EQU_MODEL
    ,EQU_NATION
    ,EQU_COMPANY
    ,EQU_YN
    ,EQU_NOTICE
    ,EQU_INDATE
    ,EQU_INUSER
    ,EQU_UPDATE
    ,EQU_UPUSER
)VALUES
(
    #{fact_cd}
    ,#{equ_cd}
    ,#{proc_cd}
    ,#{equ_nm}
    ,#{equ_no}
    ,#{equ_type}
    ,#{equ_model}
    ,#{equ_nation}
    ,#{equ_company}
    ,'Y'
    ,#{equ_notice}
    ,NOW()
    ,IFNULL(#{u_cd}, 'system')
    ,NOW()
    ,IFNULL(#{u_cd}, 'system')
)
ON DUPLICATE KEY UPDATE
    EQU_NM = #{equ_nm}
    ,PROC_CD = #{proc_cd}
    ,EQU_NO = #{equ_no}
    ,EQU_TYPE = #{equ_type}
    ,EQU_MODEL = #{equ_model}
    ,EQU_NATION = #{equ_nation}
    ,EQU_COMPANY = #{equ_company}
    ,EQU_UPDATE = NOW()
    ,EQU_UPUSER = IFNULL(#{u_cd}, 'system')
