mapper.select:
SELECT FACT_CD ,U_CD ,U_PW ,U_NM ,DEPT_CD ,DEPT_NM ,GRADE_CD ,GRADE_NM ,U_ADDR ,U_TEL ,U_MAIL ,U_INDT ,U_OUTDT ,U_YN ,U_NOTICE ,U_INDATE ,U_INUSER ,U_UPDATE ,U_UPUSER FROM MES_USER_INFO LEFT JOIN MES_DEPARTMENT_INFO USING (DEPT_CD) LEFT JOIN MES_GRADE_INFO USING (GRADE_CD) WHERE U_YN = 'Y'
AND DEPT_CD = #{dept_cd}
AND GRADE_CD = #{grade_cd}
AND ( U_CD LIKE CONCAT('%', #{search_text}, '%') OR U_NM LIKE CONCAT('%', #{search_text}, '%') OR U_ADDR LIKE CONCAT('%', #{search_text}, '%') OR U_TEL LIKE CONCAT('%', #{search_text}, '%') OR U_MAIL LIKE CONCAT('%', #{search_text}, '%') )

mapper.insert:
INSERT INTO MES_USER_INFO
(
    FACT_CD
    ,U_CD
    ,U_PW
    ,U_NM
    ,DEPT_CD
    ,GRADE_CD
    ,U_ADDR
    ,U_TEL
    ,U_MAIL
    ,U_INDT
    ,U_OUTDT
    ,U_YN
    ,U_NOTICE
    ,U_INDATE
    ,U_INUSER
    ,U_UPDATE
    ,U_UPUSER
)VALUES
(
    #{fact_cd}
    ,#{u_cd}
    ,#{u_pw}
    ,#{u_nm}
    ,#{dept_cd}
    ,#{grade_cd}
    ,#{u_addr}
    ,#{u_tel}
    ,#{u_mail}
    ,#{u_indt}
    ,#{u_outdt}
    ,'Y'
    ,#{u_notice}
    ,NOW()
    ,#{u_cd}
    ,NOW()
    ,#{u_cd}
)
ON DUPLICATE KEY UPDATE
     U_PW = #{u_pw}
    ,U_NM = #{u_nm}
    ,DEPT_CD = #{dept_cd}
    ,GRADE_CD = #{grade_cd}
    ,U_ADDR = #{u_addr}
    ,U_TEL = #{u_tel}
    ,U_MAIL = #{u_mail}
    ,U_INDT = #{u_indt}
    ,U_OUTDT = #{u_outdt}
    ,U_NOTICE = #{u_notice}
    ,U_UPDATE = NOW()
    ,U_UPUSER = #{u_cd}
