mapper.select:
SELECT FACT_CD ,ODR_CD ,COMP_CD ,COMP_NM ,PLAN_CD ,PLAN_NO ,PROD_CD ,PROD_PN ,PROD_NM ,PROD_KIND ,(SELECT CODE_NM FROM DFT_CODE_INFO WHERE CODE_ID = 'PROD_KIND' AND CODE_CD = PROD_KIND ) PROD_KIND_NM ,PROD_GROUP ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_GROUP ) PROD_GROUP_NM ,PROD_FAMILY ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_FAMILY ) PROD_FAMILY_NM ,PROD_STAND ,PROD_UNIT ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_UNIT ) PROD_UNIT_NM ,PROD_PRICE ,PROD_MAIN_COMP ,(SELECT COMP_NM FROM MES_COMPANY_INFO WHERE COMP_CD = PROD_MAIN_COMP ) PROD_MAIN_COMP_NM ,PROD_KEEP_CNT ,PROD_STOCK_CNT ,PROD_MASS_YN ,PLAN_STDT ,PLAN_EDDT ,PLAN_CNT ,PLAN_STATE ,(SELECT CODE_NM FROM DFT_CODE_INFO WHERE CODE_ID = 'MES_STATE' AND CODE_CD = PLAN_STATE ) PLAN_STATE_NM ,PLAN_NOTICE ,PLAN_INDATE ,PLAN_INUSER ,PLAN_UPDATE ,PLAN_UPUSER FROM MES_PRODUCTION_PLAN MOI JOIN MES_COMPANY_INFO USING (FACT_CD, COMP_CD) JOIN MES_PRODUCT_INFO USING (FACT_CD, PROD_CD) WHERE DATE_FORMAT(PLAN_STDT, '%Y-%m-%d') >= #{startDate} AND DATE_FORMAT(PLAN_EDDT, '%Y-%m-%d') <= #{endDate}
AND PROD_KIND = #{prod_kind}
AND PROD_FAMILY = #{prod_family}
AND PROD_GROUP = #{prod_group}
AND ( PROD_CD LIKE CONCAT('%', #{search_text}, '%') OR PROD_PN LIKE CONCAT('%', #{search_text}, '%') OR PROD_NM LIKE CONCAT('%', #{search_text}, '%') OR COMP_NM LIKE CONCAT('%', #{search_text}, '%') OR ODR_CD LIKE CONCAT('%', #{search_text}, '%') OR PROD_NO LIKE CONCAT('%', #{search_text}, '%') OR (SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_GROUP) LIKE CONCAT('%', #{search_text}, '%') OR (SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_FAMILY) LIKE CONCAT('%', #{search_text}, '%') )

SELECT FACT_CD ,PROD_CD ,PROD_PN ,PROD_NM ,PROD_KIND ,(SELECT CODE_NM FROM DFT_CODE_INFO WHERE CODE_ID = 'PROD_KIND' AND CODE_CD = PROD_KIND ) PROD_KIND_NM ,PROD_GROUP ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_GROUP ) PROD_GROUP_NM ,PROD_FAMILY ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_FAMILY ) PROD_FAMILY_NM ,PROD_STAND ,PROD_UNIT ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_UNIT ) PROD_UNIT_NM ,PROD_PRICE ,PROD_MAIN_COMP ,(SELECT COMP_NM FROM MES_COMPANY_INFO WHERE COMP_CD = PROD_MAIN_COMP ) PROD_MAIN_COMP_NM ,PROD_KEEP_CNT ,PROD_STOCK_CNT ,PROD_YN ,PROD_MASS_YN ,PROD_NOTICE ,PROD_INDATE ,PROD_INUSER ,PROD_UPDATE ,PROD_UPUSER FROM MES_PRODUCT_INFO WHERE PROD_YN = 'Y'
AND PROD_KIND = #{prod_kind}
AND PROD_GROUP = #{prod_group}
AND PROD_FAMILY = #{prod_family}
AND PROD_CD = #{prod_cd}

mapper.update:
UPDATE MES_PRODUCTION_PLAN
SET COMP_CD = #{comp_cd}
  ,PROD_CD = #{prod_cd}
  ,PLAN_NO = #{plan_no}
  ,PLAN_STDT = #{plan_stdt}
  ,PLAN_EDDT = #{plan_eddt}
  ,PLAN_CNT = #{plan_cnt}
  ,PLAN_NOTICE = #{plan_notice}
  ,PLAN_UPDATE = NOW()
  ,PLAN_UPUSER = IFNULL(#{u_cd}, 'system')
WHERE PLAN_CD = #{plan_cd}

UPDATE MES_PRODUCTION_PLAN
SET PLAN_STATE = #{plan_state}
WHERE PLAN_CD = #{plan_cd}

UPDATE MES_PRODUCTION_PLAN_PROCESS
SET PLAN_PROC_STATE = #{plan_proc_state}
WHERE PLAN_CD = #{plan_cd}
AND PLAN_PROC_CD = #{plan_proc_cd}
