mapper.select:
SELECT FACT_CD ,PLAN_CD ,PLAN_PROC_CD ,ODR_CD ,COMP_CD ,COMP_NM ,PLAN_NO ,PROD_CD ,PROD_PN ,PROD_NM ,PROD_KIND ,(SELECT CODE_NM FROM DFT_CODE_INFO WHERE CODE_ID = 'PROD_KIND' AND CODE_CD = PROD_KIND ) PROD_KIND_NM ,PROD_GROUP ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_GROUP ) PROD_GROUP_NM ,PROD_FAMILY ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_FAMILY ) PROD_FAMILY_NM ,PROD_STAND ,PROD_UNIT ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_UNIT ) PROD_UNIT_NM ,PROD_KEEP_CNT ,PROD_STOCK_CNT ,PROD_MASS_YN ,PROC_CD ,PROC_NM ,PLAN_STDT ,PLAN_EDDT ,PLAN_CNT ,PLAN_PROC_SEQ ,(SELECT COUNT(*) FROM MES_PRODUCTION_PLAN_PROCESS WHERE PLAN_CD = MPPP.PLAN_CD) TOT_PROC_CNT ,CONCAT(PLAN_PROC_SEQ, ' / ', (SELECT COUNT(*) FROM MES_PRODUCTION_PLAN_PROCESS WHERE PLAN_CD = MPPP.PLAN_CD)) TOT_PROC_CNT_NM ,PLAN_PROC_LAST_YN ,PLAN_PROC_CNT ,IFNULL(ROUND(PLAN_CNT / MPPP.PLAN_PROC_CNT), 0) PLAN_PROGRESS ,PLAN_PROC_STATE ,(SELECT CODE_NM FROM DFT_CODE_INFO WHERE CODE_ID = 'MES_STATE' AND CODE_CD = PLAN_PROC_STATE ) PLAN_PROC_STATE_NM ,PLAN_PROC_NOTICE ,PLAN_PROC_INDATE ,PLAN_PROC_INUSER ,PLAN_PROC_UPDATE ,PLAN_PROC_UPUSER FROM MES_PRODUCTION_PLAN_PROCESS MPPP JOIN MES_PRODUCTION_PLAN USING (FACT_CD, PLAN_CD) JOIN MES_PRODUCT_INFO USING (FACT_CD, PROD_CD) LEFT JOIN MES_COMPANY_INFO USING (COMP_CD, FACT_CD) JOIN MES_PROCESS_INFO USING (PROC_CD, FACT_CD) WHERE DATE_FORMAT(PLAN_STDT, '%Y-%m-%d') >= #{startDate} AND DATE_FORMAT(PLAN_EDDT, '%Y-%m-%d') <= #{endDate}
AND PROD_KIND = #{prod_kind}
AND PROD_FAMILY = #{prod_family}
AND PROD_GROUP = #{prod_group}
AND ( PROD_CD LIKE CONCAT('%', #{search_text}, '%') OR PROD_PN LIKE CONCAT('%', #{search_text}, '%') OR PROD_NM LIKE CONCAT('%', #{search_text}, '%') OR COMP_NM LIKE CONCAT('%', #{search_text}, '%') OR ODR_CD LIKE CONCAT('%', #{search_text}, '%') OR PROD_NO LIKE CONCAT('%', #{search_text}, '%') OR (SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_GROUP) LIKE CONCAT('%', #{search_text}, '%') OR (SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_FAMILY) LIKE CONCAT('%', #{search_text}, '%') )
ORDER BY PLAN_CD , PLAN_PROC_SEQ

mapper.update:
UPDATE MES_PRODUCTION_PLAN
SET COMP_CD = #{comp_cd}
  ,PROD_CD = #{prod_cd}
  ,PLAN_NO = #{result_no}
  ,PLAN_STDT = #{result_stdt}
  ,PLAN_EDDT = #{result_eddt}
  ,PLAN_CNT = #{result_cnt}
  ,PLAN_NOTICE = #{result_notice}
  ,PLAN_UPDATE = NOW()
  ,PLAN_UPUSER = IFNULL(#{u_cd}, 'system')
WHERE PLAN_CD = #{result_cd}
