mapper.select:
SELECT FACT_CD ,ODR_CD ,COMP_CD ,COMP_NM ,PROD_CD ,PROD_PN ,PROD_NM ,PROD_KIND ,(SELECT CODE_NM FROM DFT_CODE_INFO WHERE CODE_ID = 'PROD_KIND' AND CODE_CD = PROD_KIND ) PROD_KIND_NM ,PROD_GROUP ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_GROUP ) PROD_GROUP_NM ,PROD_FAMILY ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_FAMILY ) PROD_FAMILY_NM ,PROD_STAND ,PROD_UNIT ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_UNIT ) PROD_UNIT_NM ,PROD_PRICE ,PROD_MAIN_COMP ,(SELECT COMP_NM FROM MES_COMPANY_INFO WHERE COMP_CD = PROD_MAIN_COMP ) PROD_MAIN_COMP_NM ,ODR_DT ,ODR_SHIP_DT ,ODR_CNT ,ODR_PRICE ,ODR_AMT ,ODR_VAT ,ODR_VAT_YN ,ODR_NM ,ODR_TEL ,ODR_PROJECT ,ODR_STATE ,(SELECT CODE_NM FROM DFT_CODE_INFO WHERE CODE_ID = 'MES_STATE' AND CODE_CD = ODR_STATE ) ODR_STATE_NM ,ODR_NOTICE ,ODR_INDATE ,ODR_INUSER ,ODR_UPDATE ,ODR_UPUSER FROM MES_ORDER_INFO MOI JOIN MES_COMPANY_INFO USING (FACT_CD, COMP_CD) JOIN MES_PRODUCT_INFO USING (FACT_CD, PROD_CD) WHERE DATE_FORMAT(${date_type}, '%Y-%m-%d') >= #{startDate} AND DATE_FORMAT(${date_type}, '%Y-%m-%d') <= #{endDate}
AND PROD_KIND = #{prod_kind}
AND PROD_FAMILY = #{prod_family}
AND PROD_GROUP = #{prod_group}
AND ( PROD_CD LIKE CONCAT('%', #{search_text}, '%') OR PROD_PN LIKE CONCAT('%', #{search_text}, '%') OR PROD_NM LIKE CONCAT('%', #{search_text}, '%') OR COMP_NM LIKE CONCAT('%', #{search_text}, '%') OR (SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_GROUP) LIKE CONCAT('%', #{search_text}, '%') OR (SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = PROD_FAMILY) LIKE CONCAT('%', #{search_text}, '%') )

mapper.update:
UPDATE MES_ORDER_INFO
SET COMP_CD = #{comp_cd}
  ,PROD_CD = #{prod_cd}
  ,ODR_DT = #{odr_dt}
  ,ODR_SHIP_DT = #{odr_ship_dt}
  ,ODR_CNT = #{odr_cnt}
  ,ODR_PRICE = #{odr_price}
  ,ODR_AMT = #{odr_amt}
  ,ODR_VAT = #{odr_vat}
  ,ODR_VAT_YN = #{odr_vat_yn}
  ,ODR_NM = #{odr_nm}
  ,ODR_TEL = #{odr_tel}
  ,ODR_PROJECT = #{odr_project}
  ,ODR_NOTICE = #{odr_notice}
WHERE ODR_CD = #{odr_cd}

mapper.delete:
DELETE FROM MES_ORDER_INFO
WHERE ODR_CD IN

    #{item}

DELETE FROM MES_SHIP_INFO
WHERE ODR_CD IN

    #{item}

mapper.update:
UPDATE MES_ORDER_INFO
SET ODR_STATE = #{odr_state}
WHERE ODR_CD = #{odr_cd}
