mapper.select:
SELECT FACT_CD ,COMP_CD ,COMP_NM ,COMP_TYPE ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = COMP_TYPE ) COMP_TYPE_NM ,COMP_GROUP ,(SELECT BASE_DETAIL_NM FROM DFT_BASE_DETAIL WHERE BASE_DETAIL_CD = COMP_GROUP ) COMP_GROUP_NM ,COMP_BUSIN_NUM ,COMP_CEO ,COMP_BUSIN_STAT ,COMP_BUSIN_KIND ,COMP_TEL ,COMP_FAX ,COMP_ADDRESS ,COMP_EMAIL ,COMP_MANA ,COMP_MANA_TEL ,COMP_YN ,COMP_NOTICE ,COMP_INDATE ,COMP_INUSER ,COMP_UPDATE ,COMP_UPUSER FROM MES_COMPANY_INFO WHERE COMP_YN = 'Y'
AND COMP_TYPE = #{comp_type}
AND COMP_GROUP = #{comp_group}

mapper.insert:
INSERT INTO MES_COMPANY_INFO
(
    FACT_CD
    ,COMP_CD
    ,COMP_NM
    ,COMP_TYPE
    ,COMP_GROUP
    ,COMP_BUSIN_NUM
    ,COMP_CEO
    ,COMP_BUSIN_STAT
    ,COMP_BUSIN_KIND
    ,COMP_TEL
    ,COMP_FAX
    ,COMP_ADDRESS
    ,COMP_EMAIL
    ,COMP_MANA
    ,COMP_MANA_TEL
    ,COMP_NOTICE
    ,COMP_INDATE
    ,COMP_INUSER
    ,COMP_UPDATE
    ,COMP_UPUSER
)VALUES
(
    #{fact_cd}
    ,#{comp_cd}
    ,#{comp_nm}
    ,#{comp_type}
    ,#{comp_group}
    ,#{comp_busin_num}
    ,#{comp_ceo}
    ,#{comp_busin_stat}
    ,#{comp_busin_kind}
    ,#{comp_tel}
    ,#{comp_fax}
    ,#{comp_address}
    ,#{comp_email}
    ,#{comp_mana}
    ,#{comp_mana_tel}
    ,#{comp_notice}
    ,NOW()
    ,#{u_cd}
    ,NOW()
    ,#{u_cd}
)
ON DUPLICATE KEY UPDATE
    COMP_NM = #{comp_nm}
    ,COMP_TYPE = #{comp_type}
    ,COMP_GROUP = #{comp_group}
    ,COMP_BUSIN_NUM = #{comp_busin_num}
    ,COMP_CEO = #{comp_ceo}
    ,COMP_BUSIN_STAT = #{comp_busin_stat}
    ,COMP_BUSIN_KIND = #{comp_busin_kind}
    ,COMP_TEL = #{comp_tel}
    ,COMP_FAX = #{comp_fax}
    ,COMP_ADDRESS = #{comp_address}
    ,COMP_EMAIL = #{comp_email}
    ,COMP_MANA = #{comp_mana}
    ,COMP_MANA_TEL = #{comp_mana_tel}
    ,COMP_NOTICE = #{comp_notice}
    ,COMP_UPDATE = NOW()
    ,COMP_UPUSER = #{u_cd}
